mapper.select:
select  deptname, deptno, upper_deptno
from TBL_DEPT
where upper_deptno = #{deptno}

select notino, profile_color, subject, name, substr(name, -2, 2) as nickname, deptname, position, readCount, content , to_char(WRITEDATE , 'yyyy-mm-dd hh24:mi:ss' ) as writedate , notiLevel, cmtCount, showDept, fk_deptno, fk_senderno from ( select notino, fk_senderno, fk_deptno, subject, content, writedate, notiLevel, readCount, cmtCount, D.deptname as showDept from tbl_notice N join tbl_dept D on N.fk_deptno = D.deptno where status = 1
) join ( select name, deptname, position, profile_color, empno from TBL_EMPLOYEES M join TBL_DEPT D on M.fk_deptno = D.deptno
) on fk_senderno = empno order by WRITEDATE desc

select notino, subject, content
from tbl_notice
where notino = #{notino}
